import React, { useState } from "react";
import { Link } from "react-router-dom"; 
import "./Header.css";
import logo from "../Assets/Imagenes/Logo.png";
import userImage from "../Assets/Imagenes/User.png"; //Imagen de usuario temporal

const Log =1; // Variable declarada temporalmente para pruebas
//Donde 
//0 = No logueado
//1 = Logueado admin
//2 = Logueado usuario

const Header = () => {
    const [showMenu, setShowMenu] = useState(false);

    return (
      <header>
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>

        <div className="search-container">
          <input type="text" placeholder="Buscar..." className="search-input" />
        </div>

        <div className="right-container">
          {Log === 1 || Log === 2 ? ( //si ha iniciado sesi칩n
            <>
              <button>USERNAME</button>
              <div className="divider-2"></div>
              <div 
                className="user-image-container"
                onMouseEnter={() => setShowMenu(true)}
                onMouseLeave={() => setShowMenu(false)}
              >
                <img src={userImage} alt="User" className="user-image" />
                {showMenu && (
                  <div className="dropdown-menu">
                    {Log === 1 ? ( //si el usuario es admin
                      <>
                        <button>Perfil</button>
                        <div className="divider-3"></div>
                        <Link to="/register">
                        <button>Videojuegos</button>
                        </Link>
                        <div className="divider-3"></div>
                        <Link to="/register">
                        <button className="button-menu">Categorias</button>
                        </Link>
                        <div className="divider-3"></div>
                        <button>Compa침ias</button>
                        <div className="divider-3"></div>
                        <button>Plataformas</button>
                        
                      </>
                    ) : ( //si el usuario NO es admin
                      <>
                        <button>Perfil</button>
                        <div className="divider-3"></div>
                        <button>Button</button>
                      </>
                    )}
                  </div>
                )}
              </div>
            </>
          ) : ( //si el usuario NO ha iniciado sesion
            <>
              <Link to="/register">
                <button>Registrarse</button>
              </Link>
              <div className="divider"></div>
              <Link to="/login">
                <button>Iniciar Sesi칩n</button>
              </Link>
            </>
          )}
        </div>
      </header>
    );
};

export default Header;
