import React, { useState } from 'react';
import './Videojuegos.css';

const Videojuegos = () => {
  const [image, setImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className="videojuego-container">
      <div className="videojuego-left-section">
        <div className="videojuego-info-block">
          <label className="videojuego-label">NOMBRE DEL VIDEOJUEGO</label>
          <input type="text" className="videojuego-input videojuego-short2" placeholder="Ingrese el nombre del videojuego" />
        </div>
        <div className="videojuego-info-block">
          <label className="videojuego-label">AÑO DE LANZAMIENTO</label>
          <input type="text" className="videojuego-input videojuego-short" placeholder="Ingrese el año de lanzamiento" />
        </div>
        <div className="videojuego-info-block">
          <label className="videojuego-label">DESARROLLADORA</label>
          <input type="text" className="videojuego-input videojuego-short" placeholder="Ingrese la desarrolladora" />
        </div>
        <div className="videojuego-info-block">
          <label className="videojuego-label">PUBLICADORA</label>
          <input type="text" className="videojuego-input videojuego-short" placeholder="Ingrese la publicadora" />
        </div>
        <div className="videojuego-info-block">
          <label className="videojuego-label">AGE RATING</label>
          <input type="text" className="videojuego-input" placeholder="Ingrese el age rating" />
        </div>
        <div className="videojuego-info-block">
          <label className="videojuego-label">DESCRIPCION</label>
          <textarea className="videojuego-textarea" placeholder="Ingrese el age rating" />
        </div>
      </div>
      <div className="videojuego-right-section">
        <h2 className="videojuego-boxart-title">BOXART</h2>
        <div className="videojuego-boxart">
          {image ? (
            <img src={image} alt="Boxart" className="videojuego-boxart-image" />
          ) : (
            <div className="videojuego-boxart-placeholder"></div>
          )}
        </div>
        <label className="videojuego-upload-button">
          SUBIR IMAGEN
          <input type="file" className="hidden" onChange={handleImageUpload} />
        </label>
      </div>
    </div>
  );
};

export default Videojuegos;
