import React, { useState } from "react";
import "./Login-Register.css"; 
import { Link } from "react-router-dom"; 
import loginImage from "../Assets/Imagenes/LogIn-Register-Background.png"; 

const Login = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  // Manejar la selección de imagen
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setSelectedImage(imageUrl);
    }
  };

  return (
    <div className="login-register-container">
      <div className="login-register-left">
        <img src={loginImage} alt="Login-register" className="login-register-image" />

        <div className="register-overlay-text">
          <h1>REGISTRATE</h1>
        </div>
      </div>
      <div className="register-right">

        <form id="register-form">
          <div className="input-group">
            <div className="input-box">
              <label htmlFor="Email-register">Email</label>
              <input type="email" id="Email-register" required />
            </div>
            
            <div className="input-box">
              <label htmlFor="Username-register">Username</label>
              <input type="text" id="Username-register" required />
            </div>
          </div>

          <label htmlFor="Password-register">Password</label>
          <input type="password" id="Password-register" required />

          <label htmlFor="Birthdate-register">Fecha de Nacimiento</label>
          <input type="date" id="Birthdate-register" required />

          <div className="icon-container">
            <label>ICON</label>
            <label htmlFor="file-input" className="user-icon">
              {selectedImage ? (
                <img src={selectedImage} alt="User Icon" className="preview-image" />
              ) : (
                <span className="icon-placeholder">+</span>
              )}
            </label>
            <input
              type="file"
              id="file-input"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: "none" }} 
            />
          </div>

          <button type="submit" id="register-button">Registrar</button>
        </form>

        <h4>¿Ya tienes una cuenta?</h4>
        <Link to="/login">
          <button className="LoginRedirect">Inicia sesión</button>
        </Link>
      </div>
    </div>
  );
};

export default Login;
